int solve1(int R, int C) {
   return 1;
}

int solve2(int R, int C) {
   if ((R*C % 2) != 0) return 0;
   return 1;
}

int solve3(int R, int C) {
   if ((R*C % 3) != 0) return 0;
   if (R == 1) return 0;
   return 1;
}

int solve4(int R, int C) {
   if ((R*C % 4) != 0) return 0;
   if (R <= 2) return 0;
   return 1;
}

