int countEmptyDiags(unsigned int *M, int r, int c, int R,int C){
	int num = 0;
	if(r >= c){
		int _r = r - c;
		int _c = 0;
		while(_r < R && _c < C){
			if((M[_r*C+_c] & 0xf0) == 0){
				num++;
			}
			_r++;
			_c++;
		}
	}else{
		int _r = 0;
		int _c = c-r;
		while(_r < R && _c < C){
			if((M[_r*C+_c] & 0xf0) == 0){
				num++;
			}	
			_r++;
			_c++;
		}
	}
	if(r+c < C){
		int _r = 0;
		int _c = c+r;
		while(_c >= 0 && _r < R){
			if((M[_r*C+_c] & 0xf0) == 0){
				num++;
			}	
			_r++;
			_c--;
		}
	}else{
		int _r = r - (C-c);
		int _c = C-1;
		while(_c >= 0 && _r < R){
			if((M[_r*C+_c] & 0xf0) == 0){
				num++;
			}
			_r++;
			_c--;
		}
	}
	return num;
}

void fillDiags(unsigned int *M,int r,int c, int R, int C,unsigned int val){
	// fill up SE diag
	if(r >= c){
		int _r = r - c;
		int _c = 0;
		while(_r < R && _c < C){
			M[_r*C + _c] |= val;
			_r++;
			_c++;
		}
	}else{
		int _r = 0;
		int _c = c-r;
		while(_r < R && _c < C){
			M[_r*C + _c] |= val;
			_r++;
			_c++;
		}
	}
	if(r+c < C){
		int _r = 0;
		int _c = c+r;
		while(_c >= 0 && _r < R){
			M[_r*C + _c] |= val;
			_r++;
			_c--;
		}
	}else{
		int _r = r - (C-c);
		int _c = C-1;
		while(_c >= 0 && _r < R){
			M[_r*C + _c] |= val;
			_r++;
			_c--;
		}
	}
}

void try2(int index) {
	if(index==N) {
		compare();
		return;
	}
	if(Q[index]=='?') {
		int i;
		for(i=0; i<10; i++) {
			Q[index] = i + '0';
			try2(index+1);
		}
		Q[index] = '?';
	} else try2(index+1);
}

void try(int index) {
	if(index==N) {
		try2(0);
		return;
	}
	if(P[index]=='?') {
		int i;
		for(i=0; i<10; i++) {
			P[index] = i + '0';
			try(index+1);
		}
		P[index] = '?';
	} else try(index+1);
}

int chrono_sort(const void * a, const void * b){
  const act_t * A = a;
  const act_t * B = b;

  return A->start - B->start;
}

int
dcomp (const void *a, const void *b) 
{
	sen_t *x = (sen_t *)a;
	sen_t *y = (sen_t *)b;

	return y->cnt - x->cnt;
}

void print_array(char **a,int m,int n)
{
    int x,y;


    for(x=0;x<m;x++)
    {
        for(y=0;y<n;y++)
        {
            printf("%c",a[x][y]);
        }
        printf("\n");
    }
}

