void matprint(char **a,int m,int n)
{
    int i,j;
    for(i=0;i<m;i++)
    {
        for(j=0;j<n;j++)
        {
            printf("%c",a[i][j]);
        }
        printf("\n");
    }
}

void fun(char **arr,int m,int n)
{
    int x,y,f;


    for(x=0;x<m;x++)
    {        f = 1;
        for(y=0;y<n;y++)
        {
            if(arr[x][y]!='?')
            {
                f = 0;
                break;
            }
        }
        if(f)
        {
            for(y=0;y<n;y++)
            {
                arr[x][y]='a';
            }
        }
        else
        {
            for(y=1;y<n;y++)
            {
                if(arr[x][y-1]!='?' && arr[x][y]=='?')
                {
                    arr[x][y] = arr[x][y-1];
                }
            }
            if(arr[x][0]=='?')
            {
                for(y=0;y0;y--)
                {
                    arr[x][y-1]=arr[x][y];
                }
            }
        }
    }


    if(arr[0][0]=='a')
    {
        for(x=0;x0;x--)
        {
            for(y=0;y<n;y++)
            {
                arr[x-1][y]=arr[x][y];
            }
        }
    }


    for(x=1;x<m;x++)
    {
        if(arr[x][0]=='a')
        {
            for(y=0;y<n;y++)
            {
                arr[x][y]=arr[x-1][y];
            }
        }
    }


	print_array(arr,m,n);


}

void func(char **array,int m,int n)
{
    int i,j,temp, x, y;

    for(i=0;i<m;i++)
    {
        temp = 1;
        for(j=0;j<n;j++)
        {
            if(array[i][j]!='?')
            {
                temp = 0;
                break;
            }
        }
        if(temp)
        {
            for(j=0;j<n;j++)
            {
                array[i][j]='a';
            }
        }
        else
        {
            for(j=1;j<n;j++)
            {
                if(array[i][j-1]!='?' && array[i][j]=='?')
                {
                    array[i][j] = array[i][j-1];
                }
            }
            if(array[i][0]=='?')
            {
                for(j=0;j0;j--)
                {
                    array[i][j-1]=array[i][j];
                }
            }
        }
    }

    if(array[0][0]=='a')
    {
        for(i=0;i0;i--)
        {
            for(j=0;j<n;j++)
            {
                array[i-1][j]=array[i][j];
            }
        }
    }


    for(i=1;i<m;i++)
    {
        if(array[i][0]=='a')
        {
            for(j=0;j<n;j++)
            {
                array[i][j]=array[i-1][j];
            }
        }
    }


    for(x=0;x<m;x++)
    {
        for(y=0;y<n;y++)
        {
            printf("%c",array[x][y]);
        }
        printf("\n");
    }
}

void pro(char **a,int m,int n)
{
    int i,j,flag;

    for(i=0;i<m;i++)
    {
        flag = 1;
        for(j=0;j<n;j++)
        {
            if(a[i][j]!='?')
            {
                flag = 0;
                break;
            }
        }
        if(flag)
        {
            for(j=0;j<n;j++)
            {
                a[i][j]='a';
            }
        }
        else
        {
            for(j=1;j<n;j++)
            {
                if(a[i][j-1]!='?' && a[i][j]=='?')
                {
                    a[i][j] = a[i][j-1];
                }
            }
            if(a[i][0]=='?')
            {
                for(j=0;j0;j--)
                {
                    a[i][j-1]=a[i][j];
                }
            }
        }
    }

    if(a[0][0]=='a')
    {
        for(i=0;i0;i--)
        {
            for(j=0;j<n;j++)
            {
                a[i-1][j]=a[i][j];
            }
        }
    }


    for(i=1;i<m;i++)
    {
        if(a[i][0]=='a')
        {
            for(j=0;j<n;j++)
            {
                a[i][j]=a[i-1][j];
            }
        }
    }
    matprint(a,m,n);
}

