int main(int argc, char *argv[])
{
	size_t i, T;
	size_t D, N, j;
	double answer;
	struct horse horses[NMAX];

	scanf("%llu", &T);
	for(i = 0; i < T; i++) {
		scanf("%llu %llu", &D, &N);

		for(j = 0; j < N; j++)
			scanf("%u %u", &horses[j].position, &horses[j].speed);

		answer = solve(D, horses, N);
		printf("Case #%u: %.6f\n", i + 1, answer);
	}

	return EXIT_SUCCESS;
}

int main(int argc, char *argv[])
{
	size_t T, i;
	size_t N, K, j;
	double U;
	double core[NMAX];
	double answer;

	scanf("%llu", &T);
	for(i = 0; i < T; i++) {
		scanf("%llu %llu", &N, &K);

		scanf("%lf", &U);
		for(j = 0; j < N; j++)
			scanf("%lf", &core[j]);

		answer = solve(core, N, K, U);
		printf("Case #%u: %.6f\n", i + 1, answer);
	}
}

int cmp(const void *a, const void *b)
{
	double *da = (double*)a;
	double *db = (double*)b;

	if(*da == *db) return 0;
	return *da > *db ? -1 : 1;
}

int compare(const void *a,const void *b){
  double *A = (double*)a;
  double *B = (double*)b;
  if(*B<*A)
    return 1;
  if(*A==*B)
    return 0;
  return -1;
}

