ubig getsubsetindex( ubig K, ubig C, ubig* sublist, int num ) {
    int i;
    ubig result = 0;
    for (i = 0; i < num; i++) {
        result += sublist[i] * bpow(K, C - i - 1);
    }
    return result;
}

LL finishcount(int x,LL mid){
  int a;
  LL finishcnt=0;
  for(a=0;a<x;a++){
    finishcnt+=mid/barber[a];
  }
  return finishcnt;
}

int max(int* P, int len)
{
    int max = 0;
    int i;
    for (i = 0; i < len; i++) {
        max = max<P[i] ? P[i] : max;
    }

    return max;
}

int s(int* num,int n)
{
	int i,total=0;
	for(i=0;i<n;i++)
	{
		total+=num[i];
	}

	return total;
}

