static int cycle_test(int start) {
	bool used[1000] = {0};
	int curr = start;
	int ans = 1;
	while(1){
		used[curr] = true;
		int next = bff[curr];
		if(next == start) {
			return ans;
		}
		if(used[next]){
			return 1;
		}
		curr = bff[curr];
		ans++;
	}
}

static int chain_test(int start, int* end_pair) {
	bool used[1000] = {0};
	int curr = start;
	int prev = -1;
	int ans = 0;
	while(1){
		used[curr] = true;
		int next = bff[curr];
		if(next == prev) {
			*end_pair = curr;
			return ans;
		}
		if(used[next]){
			return 0;
		}
		prev = curr;
		curr = bff[curr];
		ans++;
	}
}

int gcd(int a,int b){
  int c;
  while ( a != 0 ) {
     c = a; a = b%a;  b = c;
  }
  return b;
}

long hcf(long a, long b)
{
	int temp;
	while(a % b != 0)
	{
		temp = a%b;
		a = b;
		b = temp;
	}
	return b;
}

int get_int(){
	char c;
	while (c=getchar(),c<'0'||c>'9'){}
	int result = 0;
	while (c>='0'&&c<='9'){
		result = result*10+c-'0';
		c = getchar();
	}
	return result;
}

