int main ( )
{
	int ntc, test, n;
	scanf ( "%d", &ntc );
	for ( test = 1; test <= ntc; ++test ) {
		scanf ( "%d%s", &n, s );
		int ans = 0, i, sum = 0;
		for ( i = 0; i <= n; ++i ) {
			if ( s[i] != '0' )
				if ( sum < i ) {
					ans += i-sum;
					sum = i;
				}
			sum += s[i]-'0';
		}

		printf ( "Case #%d: %d\n", test, ans );
	}
	return 0;
}

int main()
{
  int n, i, j, T, now, need;
  char s[SMAX+5];
  scanf("%d",&T);
  for(i=0;i<T;i++) {

    now = need = 0;
    scanf("%d%s",&n, s); 
    now = s[0]-'0';

    for(j=1;j<=n;j++){

      if( now < j ){
	need += j-now;
	now = j;
      }
      now += s[j]-'0';
    }
    printf("Case #%d: %d\n", i+1, need);
  }
  return 0;
}

