long long cutting_num(long long t) {
    long long acc = 0LL;
    int i;
    for(i = 0; i < B; ++i) {
        acc += t/(long long)(M[i]) + 1;
    }
    return acc;
}

long lcmOfAll(int * m, int n)
{
	long ans = 1;
	int i;
	for(i = 0; i<n; ++i)
	{
		ans = lcm(ans, m[i]);
	}
	return ans;
}

