int digs(ll x)
{
	int cnt = 0;
	while(x)
	{
		x /= 10;
		cnt++;
	}
	return cnt;
}

int leng(long long rnum)
{
	int x=0;
		while(rnum)
		{
         x++;
         rnum=rnum/10;
		 }
    return x;
}

ll rev(ll x)
{
	ll y = 0;
	while(x)
	{
		y = 10 * y + x % 10;
		x /= 10;
	}
	return y;
}

